<form class="contact_us" method="post" action="#" id="signup_Customer_form" style="display: none;"><div id="signup_Customer_form_Alert" style="display: none;"><div class="alert alert-danger" style="">Please check login details are invalid!</div></div><div class="form-group"><input type="text" placeholder="<?php echo lang('first') ?>" class="form-control required" name="firstname" required="" id="FBfirst_name" autocomplete="off"></div><div class="form-group"><input type="text" placeholder="<?php echo lang('last') ?>" class="form-control required" name="lastname" required="" id="FBlast_name" autocomplete="off"></div><div class="form-group"></div><div class="form-group"><input type="password" title="<?php echo lang('password_title') ?>" pattern=".{8,}" placeholder="<?php echo lang('password_placeholder') ?>" name="password" id="password" class="form-control required" required="" autocomplete="off"></div><div class="formbox"><div class="checkbox"><div class="form-group" id="signup_Customer_form_recaptcha"><?php echo $recaptcha; ?></div></div></div><button type="submit" class="btn btn-default smt-btn">Submit</button></form>